async function wait(ms) {
    return new Promise(resolve => setTimeout(resolve, ms));
}
async function retry(delay, ref, ...args) {
    while (true) {
        try {
            await wait(delay);
            return ref(...args);
        } catch (error) {
            console.error(error);
        }
    }
}

function changeMode() {
    GM_setValue('autoMode', (GM_getValue('autoMode', 2) + 1) % 3);
    document.getElementById("autoModeButton").innerText = "Mode: " + ["Manual", "Auto", "Full Auto"][GM_getValue('autoMode', 2)];
    setTimeout(() => {
        document.getElementById("navbarSettings").click();
    }, 100);
};

function answer() {
    const question = document.querySelector(".card-header.question-card-header.pt-4.pb-4.border-secondary > .row.m-0 > .col").innerHTML.replace(/\s+/g, ' ').trim();
    if (document.getElementById("text-answer")) {
        let answertxt = null;
        for (let i of data[question]) {
            if (i[0] === ">") {
                answertxt = i.slice(1); break;
            }
        }
        if (answertxt !== null) {
            retry(0, () => {
                document.getElementById('text-answer').value = answertxt;
                if (GM_getValue('autoMode', 2) === 1) {
                    retry(0, () => {
                        document.getElementById("submit-answer-button").click();
                    });
                } else if (GM_getValue('autoMode', 2) === 2) {
                    retry(0, () => {
                        document.getElementById("submit-answer-button").click();
                        retry(0, () => {
                            if (document.getElementById("next")) {
                                document.getElementById("next").click();
                            } else {
                                document.getElementById("finish").click();
                            }
                        });
                    });
                }
            });
        }
    } else {
        // WARNING: Will break if answer with needed id is a dupe question [1] or if there are 2 answer dupes present [2]
        // [1] Value is a list of answers (>1)
        // [2] The answer buttons have more than one identical duplicated answer

        const buttons = {};
        const buttonElements = document.querySelector(".card-body.question-card-body").querySelectorAll("button.btn.btn-assess-choice.btn-block");
        let dupKey = null;

        buttonElements.forEach((button) => {
            const key = button.parentElement.parentElement.parentElement.querySelector(".m-1.pt-5.pb-5.pr-2.pl-2.border.border-info.flex-grow-1").innerHTML.trim();
            if (key in buttons) { dupKey = key; };
            const value = button.id;
            buttons[key] = value;
        });

        let answer = null;
        if (dupKey && data[question][0].split(`##id=`)[0] == dupKey) {
            answer = document.getElementById(data[question][0].split(`##id=`)[1]);
        } else {
          console.log(question, data[question]);
            for (let i of data[question]) {
                if (i in buttons) {
                    answer = document.getElementById(buttons[i]);
                    break;
                }
            }
        }
        if (GM_getValue('autoMode', 2) === 1) {
            retry(0, () => {
                answer.click();
            });
        } else if (GM_getValue('autoMode', 2) === 2) {
            retry(0, () => {
                answer.click();
                retry(0, () => {
                    if (document.getElementById("next")) {
                        document.getElementById("next").click();
                    } else {
                        document.getElementById("finish").click();
                    }
                });
            });
        } else {
            retry(0, () => {
                answer.style.cssText = `background-color: #5787AE !important;`
            });
        }
    }
};

retry(0, () => {
    if (!document.getElementById('autoModeButton')) {
        const settings = document.getElementById("navbarSettings").parentElement.querySelector(".dropdown-menu");
        const btn = document.createElement("button");
        const txt = document.createTextNode("Mode: " + ["Manual", "Auto", "Full Auto"][GM_getValue('autoMode', 2)]);
        btn.appendChild(txt);
        btn.onclick = changeMode;
        btn.style.cssText = `border: none; width: 100%; height: 100%;`;
        btn.id = "autoModeButton";
        settings.appendChild(btn);
    }
});

if (window.location.href.includes("question-page")) {
    if (GM_getValue('autoMode', 2) === 2) {
        // Auto refresh on full auto
        setTimeout(() => {
            retry(500, location.replace(window.location.href));
        }, 7000);
    }
    retry(0, answer);
}
