function changeMode() {
    GM_setValue('autoMode', (GM_getValue('autoMode', 0) + 1) % 3);
    document.getElementById("autoModeButton").innerText = "Mode: " + ["Manual", "Auto", "Full Auto"][GM_getValue('autoMode', 0)];
    setTimeout(() => {
        document.getElementById("navbarSettings").click();
    }, 100);
};

document.body.onload = answer;
function answer() {
    const settings = document.getElementById("navbarSettings").parentElement.querySelector(".dropdown-menu");
    const btn = document.createElement("button");
    const txt = document.createTextNode("Mode: " + ["Manual", "Auto", "Full Auto"][GM_getValue('autoMode', 0)]);
    btn.appendChild(txt);
    btn.onclick = changeMode;
    btn.style.cssText = `border: none; width: 100%; height: 100%;`;
    btn.id = "autoModeButton";
    settings.appendChild(btn);

    const question = document.querySelector(".card-header.question-card-header.pt-4.pb-4.border-secondary > .row.m-0 > .col").innerHTML.replace(/\s+/g, ' ').trim();
    if (document.getElementById("text-answer")) {
        let answertxt = "";
        for (let i of data[question]) {
            if (i[0] === ">") {
                answertxt = i.slice(1); break;
            }
        }
        if (answertxt) {
            setTimeout(() => {
                document.getElementById('text-answer').value = answertxt;
            }, 500);
            if (GM_getValue('autoMode', 0) === 1) {
                setTimeout(() => {
                    document.getElementById("submit-answer-button").click();
                }, 1000);
            } else if (GM_getValue('autoMode', 0) === 2){
                setTimeout(() => {
                    document.getElementById("submit-answer-button").click();
                }, 1000);
                setTimeout(() => {
                    if (document.getElementById("next")) {
                        document.getElementById("next").click();
                    } else {
                        document.getElementById("finish").click();
                    }
                }, 1500);
            }
        }

    } else {
        const buttons = Array.from(document.querySelector(".card-body.question-card-body").querySelectorAll("button.btn.btn-assess-choice.btn-block")).reduce((acc, i) => {
            acc[i.parentElement.parentElement.parentElement.querySelector(".m-1.pt-5.pb-5.pr-2.pl-2.border.border-info.flex-grow-1").innerHTML.trim()] = i.id;
        return acc; }, {});
        let answer = null;
        for (let i of data[question]) {
            if (i in buttons) {
                answer = document.getElementById(buttons[i]);
                break;
            }
        }
        if (GM_getValue('autoMode', 0) === 1) {
            setTimeout(() => {
                answer.click();
            }, 1000);
        } else if (GM_getValue('autoMode', 0) === 2) {
            setTimeout(() => {
                answer.click();
            }, 1000);
            setTimeout(() => {
                if (document.getElementById("next")) {
                    document.getElementById("next").click();
                } else {
                    document.getElementById("finish").click();
                }
            }, 1500);
        } else {
            answer.style.cssText = `background-color: #5787AE !important;`
        }
    }
};
